    // });


    return (
      <div className="signform ">
        <div className="container " id="container">
          <div className="form-container sign-up-container ">
            <form
              method="post"
              action="http://localhost:3000/user/auth/signup"
              className="form1"
              onSubmit={handleSubmit}
            >
              <h1>Create Account</h1>
              <div className="social-container">
                <Link
                  to="http://localhost:3000/user/auth/facebook"
                  className="social"
                >
                  <i className="fab fa-facebook-f"></i>
                </Link>
                <Link
                  to="http://localhost:3000/user/auth/google"
                  className="social"
                >
                  <i className="fab fa-google-plus-g"></i>
                </Link>
                {/* <a to="#" className="social"><i className="fab fa-linkedin-in"></i></a> */}
              </div>
              <span>or use your email for registration</span>
              <input
                type="text"
                placeholder="Name"
                name="Name"
                value={userSignUpInfo.Name}
                onChange={changeUserSignUpInfo}
              />
              <input
                type="email"
                placeholder="Email"
                name="Email"
                value={userSignUpInfo.Email}
                onChange={changeUserSignUpInfo}
              />
