import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { AddBlogsAction } from "../store/actions";
import Button from "./Button";
import Input from "./Input";
import axios from 'axios';
export default function AddBlog() {

  // const blogs = useSelector((state) => state.blogs);
  // const dispatch = useDispatch();
  // const addBlog = (params) => {
  //   dispatch(AddBlogsAction(params));
  // };

  const [inputValue, setInputValue] = useState({
    blogSubject: "",
    blogContent:"",
  });
  const handleChange = (event) =>{
    const { value, name } = event.target;
    setInputValue((previous) => {
      return {
        ...previous,
        [name]: value,
      };
    });
  }
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(inputValue);
    axios
      .post("http://localhost:3000/user/addPost", inputValue)
      .then((req) => {
        console.log(req);
        if (req.data.Success === true) {
          console.log("hhkhkhkhk");
          // props.history.push("/MyProfile");
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };
  
  useEffect(() => {
  }, []);

  return (
    <div className="addBlog">
      <input type="checkbox" id="modal" />
      <p class="blurp">Add new post</p>
      <label for="modal" class="modal-btn">
        <i class="fas fa-plus-circle fa-1x"></i>
      </label>
      {/* <div class="modal-bg"></div> */}
      <label for="modal" class="modal-bg"></label>
      <div class="modal-content">
        <header>
          <h3>Add new post</h3>
        </header>
        <article class="content">
          <form method="post" onSubmit={handleSubmit}>
            <div class="form-group">
              <label for="blogSubject">Blog Subject</label>
              <Input
                type="text"
                class="form-control"
                name="blogSubject"
                id="blogSubject"
                placeHolder="Type the subject of your blog here"
                value={inputValue.blogSubject}
                onChange={handleChange}
              />
            </div>
            <div class="form-group">
              <label for="blogContent">Blog Content</label>
              <textarea
                class="form-control"
                name="blogContent"
                id="blogContent"
                rows="3"
                placeHolder="Type the blog information"
                value={inputValue.blogContent}
                onChange={handleChange}
              ></textarea>
            </div>
            <button
              type="submit"
              className="btn btn-success"
            >
              ADD
            </button>
          </form>
        </article>

        <footer>
          {/* <Button type="submit" value="Accept" class="button success" /> */}
          {/* <label for="modal" className="button danger">
            Decline
          </label> */}
        </footer>
      </div>
    </div>
  );
}
