import React, { useEffect } from "react";

export default function Dropdown(props) {
  
  useEffect(() => {
    const inputField = document.querySelector(".chosen-value");

    const dropdown = document.querySelector(".value-list");

    const dropdownArray = [...document.querySelectorAll("li")];

    console.log(typeof dropdownArray);

    dropdown.classList.add("open");
    inputField.focus(); // Demo purposes only
    let valueArray = [];
    dropdownArray.forEach((item) => {
      valueArray.push(item.textContent);
    });

    const closeDropdown = () => {
      dropdown.classList.remove("open");
    };

    inputField.addEventListener("input", () => {
      dropdown.classList.add("open");
      let inputValue = inputField.value.toLowerCase();
      let valueSubstring;
      if (inputValue.length > 0) {
        for (let j = 0; j < valueArray.length; j++) {
          if (
            !(
              inputValue.substring(0, inputValue.length) ===
              valueArray[j].substring(0, inputValue.length).toLowerCase()
            )
          ) {
            dropdownArray[j].classList.add("closed");
          } else {
            dropdownArray[j].classList.remove("closed");
          }
        }
      } else {
        for (let i = 0; i < dropdownArray.length; i++) {
          dropdownArray[i].classList.remove("closed");
        }
      }
    });

    dropdownArray.forEach((item) => {
      item.addEventListener("click", (evt) => {
        inputField.value = item.textContent;
        dropdownArray.forEach((dropdown) => {
          dropdown.classList.add("closed");
        });
      });
    });

    inputField.addEventListener("focus", () => {
      inputField.placeholder = "Type to filter";
      dropdown.classList.add("open");
      dropdownArray.forEach((dropdown) => {
        dropdown.classList.remove("closed");
      });
    });

    inputField.addEventListener("blur", () => {
      inputField.placeholder = "Select state";
      dropdown.classList.remove("open");
    });

    document.addEventListener("click", (evt) => {
      const isDropdown = dropdown.contains(evt.target);
      const isInput = inputField.contains(evt.target);
      if (!isDropdown && !isInput) {
        dropdown.classList.remove("open");
      }
    });
  }, [valueSubstring]);

  
  return (
    <div className="dropdownn">
      <form>
        <input
          class="chosen-value"
          type="text"
          value=""
          placeholder="Type to filter"
        />
        <ul class="value-list">
          {props.mapItems.map((item, index) => {
            return <li key={index}>{item}</li>;
          })}
        </ul>
      </form>
      {/* <div className="select-box__current w-100" tabindex="0">
        {props.mapItems.map((item, index) => {
          return (
            <div className="select-box__value">
              <input
                className="select-box__input"
                type="radio"
                id={item + index}
                value={item}
                name={props.name}
                checked="checked"
              />
              <p className="select-box__input-text">{item}</p>
            </div>
          );
        })}
        <img
          className="select-box__icon"
          src="http://cdn.onlinewebfonts.com/svg/img_295694.svg"
          alt="Arrow Icon"
        />
      </div>
      <ul className="select-box__list">
        {props.mapItems.map((item, index) => {
          return (
            <li>
              <label className="select-box__option" for={item + index}>
                {item}
              </label>
            </li>
          );
        })}
      </ul> */}
    </div>
  );
}
