import React, { Suspense, lazy } from "react";
import { Switch } from "react-router-dom";
import { GuardProvider, GuardedRoute } from 'react-router-guards';

const LoginIn = lazy(() => import('../pages/Sign/LoginIn'))




export function Routes() {

    const requireLogin = (to, from, next) => {
        if (to.meta.auth) {
            if (getIsLoggedIn()) {
                next();
            }
            next.redirect('/LoginIn');
        } else {
            next();
        }
    };

    const getIsLoggedIn = () => {
        const InLocalStorage  = localStorage.getItem('email');
        if(InLocalStorage === undefined || InLocalStorage==='' || InLocalStorage ===null ) return false ;
        
        else  return true;
    }
    return (

        // <Switch>
        //     <Route path="/" exact component={Home} />
        //     <Route path="/About" exact component={About} />
        //     <Route path="/LoginIn" exact component={LoginIn} />
        // </Switch>
        <Suspense fallback={<div>Loading...</div>} >
            <GuardProvider guards={[requireLogin]}>
                <Switch>
                    <GuardedRoute  path="/" exact component={Home} />
                    <GuardedRoute  path="/About" exact component={About} meta={{ auth: true }}/>
                    <GuardedRoute  path="/LoginIn" exact component={LoginIn} />
                    <GuardedRoute  path='/BookFinder' exact component={BookFinder} meta={{ auth: true }} />
                    <GuardedRoute  path='/ToDo' exact component={ToDo} meta={{ auth: true }} />
                    <GuardedRoute  path="/Products" exact component={Product} />


                </Switch>
            </GuardProvider>
        </Suspense>
    );
}
